import React, { useRef } from 'react';
import { auth } from '../firebase';
import forgetImg from '../images/forgetImg.png';
function ForgetPassword() {
  const emailRef = useRef(null);
  const handleSubmit = (e) => {
    e.preventDefault();
    auth
      .sendPasswordResetEmail(emailRef.current.value)
      .then((emailRef.current.value = ''));
  };
  return (
    <main>
      <div className="login-area">
        <div className="Login-main-container">
          <div className="Login-svg">
            <img
              src={forgetImg}
              alt="Forget Password Image"
              className="login"
            />
          </div>
          <div className="Login-form">
            <form>
              <h1>Forgot Password ?</h1>
              <div className="Login-form-group">
                {/* <label htmlFor="Email">Email-id :- </label> */}
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your Email"
                  id="email"
                  ref={emailRef}
                />
              </div>
              <div className="Login-form-group">
                <input
                  type="checkbox"
                  id="policy"
                  name="policy"
                  value="privacypolicy"
                />
                <label htmlFor="policy"> I agree to the Privacy Policy</label>
              </div>
              <div className="Login-form-group">
                <input
                  onClick={handleSubmit}
                  type="submit"
                  value="Submit"
                  name="submit"
                />
              </div>
              <div className="Login-form-group">
                <p>Your Password will be send to your mail</p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </main>
  );
}

export default ForgetPassword;
