import React, { useRef, useState } from 'react';
import { auth } from '../firebase';
import signup from '../images/signup.png';

function Signup() {
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const cpasswordRef = useRef(null);
  const nameRef = useRef(null);
  const [message, setMessage] = useState(null);
  const [hasError, setError] = useState(false);
  let reg = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!_%*?&])[A-Za-z\d@$!_%*?&]{8,}$/;

  const register = (e) => {
    e.preventDefault();
    if (
      nameRef.current.value == '' ||
      emailRef.current.value == '' ||
      passwordRef.current.value == '' ||
      cpasswordRef.current.value == ''
    ) {
      setMessage('Please fill all the fields');
      setError(true);
    } else {
      if (reg.test(passwordRef.current.value)) {
        if (passwordRef.current.value == cpasswordRef.current.value) {
          auth
            .createUserWithEmailAndPassword(
              emailRef.current.value,
              passwordRef.current.value
            )
            .then(() => {
              setMessage('');
              setError(false);
              auth.currentUser
                .updateProfile({
                  displayName: nameRef.current.value,
                })
                .then((user) => {
                  // console.log(user);
                  // alert('User registered');
                });
            })
            .catch((error) => {
              setMessage(error.message);
              setError(true);
              // document.getElementById('email').focus();
            });
        } else {
          setMessage('Password Does Not Match');
          setError(true);
        }
      } else {
        setMessage(
          'Password must be Minimum 8 characters, at least one uppercase letter, one lowercase letter, one number and one special character:'
        );
        setError(true);
      }
    }
  };
  return (
    <main>
      <div className="login-area">
        <div className="Login-main-container">
          <div className="Login-svg">
            <img src={signup} className="login"></img>
          </div>
          <div className="Login-form">
            <form>
              <h1>Sign Up</h1>
              <div className="Login-form-group">
                <input
                  type="first Name"
                  name="fname"
                  placeholder="Enter your First Name"
                  id="fname"
                  ref={nameRef}
                />
              </div>
              <div className="Login-form-group">
                <input
                  ref={emailRef}
                  type="email"
                  name="email"
                  placeholder="Enter your Email"
                  id="email"
                />
              </div>
              <div className="Login-form-group">
                <input
                  ref={passwordRef}
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  id="password"
                />
              </div>{' '}
              <div className="Login-form-group">
                <input
                  ref={cpasswordRef}
                  type="password"
                  name="cpassword"
                  placeholder="Confirm Your Password"
                  id="cpassword"
                />
              </div>
              {message != null && (
                <div className="Login-form-group">
                  <p className={hasError ? 'reset-pass-red' : ''}>{message}</p>
                </div>
              )}
              <div div className="Login-form-group">
                <input
                  onClick={register}
                  type="submit"
                  value="Sign Up"
                  name="Continue"
                />
              </div>
            </form>
          </div>
        </div>
      </div>
    </main>
  );
}

export default Signup;
